function simpleMethod(number) {
	let n = 0;
	let success = false;
	do {let nsquared = n*n;
		if (nsquared === number) {
			success = true;
		} else {
			n++;
		}
	} while (success === false)
	return n;
}

function betterMethod(number) {
	let low = 1;
	let high = number;
	let guess = 0;
	let success = false;
	do {
		guess = (low + high) / 2;
		if (guess**2 === number) {
			success = true;
		} else if (guess**2 > number) {
			high = guess;
		} else {
			low = guess;
		}
	} while (success === false)
	return guess;
}

